mapper.insert:
INSERT INTO bookstore.member_mgt(
    member_idx
    ,member_name
    ,member_email
    ,member_id
    ,member_password
    ,member_phone
    ,member_birthday
    ,member_gender
    ,member_postal_code
    ,member_post_address
    ,member_detailed_address	
)
VALUES(
    NEXTVAL('seq_member_idx')
    ,#{memberName}
    ,#{memberEmail}
    ,#{memberId}
    ,#{memberPassword}
    ,#{memberPhone}
    ,#{memberBirthday}
    ,#{memberGender}
    ,#{memberPostalCode}
    ,#{memberPostAddress}
    ,#{memberDetailedAddress}
);

mapper.update:

    UPDATE 
    	bookstore.member_mgt
	SET 
    	member_password = #{securePasswordChange}
	WHERE
		member_id = #{memberId}
